  }
  console.log(location.key)

  return (
    <div className="App">
        <meta name="viewport" content="width=device-width, initial-scale=1"></meta>
        <link rel="stylesheet" href="https://www.w3schools.com/w3css/4/w3pro.css"></link>

       {/* <p class="app-title">
