//FIREBASE CONFIG//
import firebase from 'firebase';
import 'firebase/firestore';


var firebaseConfig = {
    apiKey: process.env.REACT_APP_apiKey,
    authDomain: process.env.REACT_APP_authDomain,
    projectId: process.env.REACT_APP_projectId,
    storageBucket: process.env.REACT_APP_storageBucket,
    messagingSenderId: process.env.REACT_APP_messagingSenderId,
    appId: process.env.REACT_APP_appId,
    measurementId: process.env.REACT_APP_measurementId
};

firebase.initializeApp(firebaseConfig)


//DATABASE INIT
const firestore = firebase.firestore();
var database = firebase.database();

const chatroomsDB = firestore.collection('chatrooms');
const usersDB = firestore.collection('users');
//const messagesRef = firestore.collection('messages');


//Functional groups
class FirebaseUtil  {

     static async getUserToken(){

          if(!localStorage.getItem("user-token")){

              let userID = await usersDB.add({
                  name: "none",
                  type: "annon"
              }).then(res =>{
                  localStorage.setItem("user-token", res.id)
                  console.log("generating new profile: ", res.id)
                  return res.id
              })

              return userID

          }else{
                console.log("returning user: ", localStorage.getItem("user-token"))
                return localStorage.getItem("user-token");
          }
    }

    static async createRoom(userName, adminId, roomName){

        const apiCompletionPromise = chatroomsDB.add({
            admin: userName,
            adminId: adminId,
            roomName: roomName,
            type: "temporary",
            timeStamp: ""//firestore.FieldValue.serverTimestamp()

        })

        return apiCompletionPromise

  }



}


export {
    FirebaseUtil
}
