class UserService  {



    static token(){
        return localStorage.getItem("user-token")
    }

    static getUserID(){
        return localStorage.getItem("user-token")
    }

    static setUserID(ID){
        localStorage.setItem("user-token", ID)
    }

    static user(){
        return {userName: localStorage.getItem("user-name"), userID: this.token()}
    }

    static login(userID, userName){
        localStorage.setItem("userName", userName)
        localStorage.setItem("userID", userID)
    }




}


export {
    UserService
}
