import './chatroom.css';
import Room from './RoomModel'


import React, { useState,  useEffect} from 'react';
import io from 'socket.io-client';


import ChatMessage from './ChatMessage';



function Chat(props) {

    const [roomCode, setRoomCode] = useState("")
    const [room, setRoom] = useState(true)
    const [messages, setMessages] = useState([{id:"2", text:'heyyyo'}])
    const [isAdmin, setIsAdmin] = useState(false)
    //const socket = props.socket;


    useEffect(() => {

        setRoom(props.data.room)

        const socket = io("http://192.168.1.9:3000", { transport: ['websocket']}) ;

        /*
        //url info
        let roomCode_ = query_params(window.location.href)


        //variables from local sotrage
        //let roomCode_ = localStorage.getItem("room-code")
        let isAdmin_ = localStorage.getItem('create-room')
        let roomName_ = localStorage.getItem("room-name")
        let userName_ = localStorage.getItem("user-name")
        let room_ = {
            name:roomName_,
            admin: userName_,
            password:"xxx",
            code: roomCode_
        }

                //set states
        if(isAdmin_)setIsAdmin(true)
        setRoomCode(roomCode_)


        */


        //socket functions
        socket.on('connect', (connection) => {

            if(isAdmin_)socket.emit('create-room', roomCode_)
            else socket.emit('join-room', roomCode_)


            socket.on('backlog-messages', (messages_) => {
                setMessages(messages_)
            });

            socket.on('broadcasted-message', (message) => {
                setMessages([...messages, message])
            });

            socket.on('room-cldsed', (message) => {
                //TODO:
            });
      });



    }, []);




    function query_params(queryString){

        var queryParts = queryString.split('?')
        var pairsString;
        var query = {}

        console.log(queryParts)
        if(queryParts.length > 1) pairsString = queryParts[1].split('=');
        console.log(pairsString)

        let i=0;
        while(i <pairsString.length){
            query[pairsString[i]] = pairsString[i+1]
            i+=2;
        }
        console.log(query)

        return query
    }








  return (
    <div class="root">
    {messages && room?
        <div class="chatroom-view">
        <div class="title-view">
            <p class="title-text">{room.room_name}</p>
        </div>
        <div class="chatroom-messages-view">
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={false}
            ></ChatMessage>
                    <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={false}
            ></ChatMessage>
                                <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            {messages?
             messages.map(msg => {
            <ChatMessage key={msg.id} text={msg.text} userName={""} isUser={false}/>
            }):null

            }

            {messages?
            <div>
                {messages.map(msg=> <ChatMessage key={msg.id} text={msg.text} userName={""} isUser={false}/>)}
                </div>
                :null
            }




        </div>
        <div class="typed-message-view">
            <input class="typed-message-input"></input><i class="material-icons send-message-btn">&#xe163;</i>


        </div>

    </div>:null
}
    </div>
  );
}

export default Chat;
