import { BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom';
import React, { useState,  useEffect} from 'react';
import { useHistory } from "react-router-dom";
import { withRouter } from "react-router";
import { Modal, Drawer, Button } from 'antd';
import "antd/dist/antd.css";




import styleSheet from '../Styles/StyleSheet'
import JoinRoom from './JoinRoom'
import CustomInput from './CustomInput'

import '../Styles/root-themes.css';
import './Landing.css';
import './LandingPrompts.css';


function LandingPrompt(props) {

  const [preRoomModalVisible, setPreRoomModalVisible] = useState(false);

  const showPreRoomModal = () => {
    console.log("clicked")
    setPreRoomModalVisible(true);
  };



  function removeToken(){
    localStorage.removeItem("persistentToken")
  }

  return (
      <div>
          <div class="root">

          <div className="landing-header">
            <button className="root-theme-button-sm root-theme-bg-dark-blue register-button">create account</button>
          </div>



          <div class="login-options-view">
          <JoinRoom showPrompt={showPreRoomModal} {...props}></JoinRoom>
          <h4 className="title-create-room">Create a room instead?</h4>
            <button  
              className="root-theme-button-sm button-create-room"
              onClick= {()=> {props.history.push("/createroom")}} 
            > 
              Create Room
            </button>


          </div>


      </div>


    <Drawer 
        closable={true} 
        title="" 
        footer={null} 
        isible={preRoomModalVisible}
        getContainer={false}
        style={{ position: 'absolute' }}
        placement="bottom"


        >

          <CustomInput
                title="Username."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Password."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Number."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Access Code."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <button >
              Create account.
            </button>
      </Drawer>
    </div>


  );
}

export default withRouter(LandingPrompt);


