import './chatroom.css';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';
import React, { useState,  useEffect} from 'react';
import io from 'socket.io-client';


import ChatMessage from './ChatMessage';





function Chat(props) {

    const [roomCode, setRoomCode] = useState("")
    const [room, setRoom] = useState("")
    const [messages, setMessages] = useState([])
    const [isAdmin, setIsAdmin] = useState(false)
    //const socket = props.socket;


    useEffect(() => {

        const socket = io("http://192.168.1.9:3000", { transport: ['websocket']}) ;

        let roomCode_ = localStorage.getItem("room-code")
        let isAdmin_ = localStorage.getItem('create-room')

        if(isAdmin_)setIsAdmin(true)
        setRoomCode(roomCode_)


        socket.on('connect', (connection) => {


            console.log("connected")
            console.log(isAdmin)
            socket.emit('create-room', "one")

            if(isAdmin)socket.emit('create-room', roomCode_)
            else socket.emit('join-room', roomCode_)


            socket.on('incoming-message', (connection) => {

              //TODO: update the list of users had

            });
      });



    }, []);




    function query_params(queryString){

        var queryParts = queryString.split('?')
        var pairsString;
        var query = {}

        console.log(queryParts)
        if(queryParts.length > 1) pairsString = queryParts[1].split('=');
        console.log(pairsString)

        let i=0;
        while(i <pairsString.length){
            query[pairsString[i]] = pairsString[i+1]
            i+=2;
        }
        console.log(query)

        return query
    }








  return (
    <div class="root">
    {messages && room?
        <div class="chatroom-view">
        <div class="title-view">
            <p class="title-text">{room.room_name}</p>
        </div>
        <div class="chatroom-messages-view">
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={false}
            ></ChatMessage>
                    <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={false}
            ></ChatMessage>
                                <ChatMessage
                text={"Hi, Sweet! So, what do you wanna do today?Sweet! "}
                userName="name"
                isUser={true}
            ></ChatMessage>
            {messages && messages.map(msg => <ChatMessage key={msg.id} text={msg.message} userName={msg.userID} />)}




        </div>
        <div class="typed-message-view">
            <input class="typed-message-input"></input><i class="material-icons send-message-btn">&#xe163;</i>


        </div>

    </div>:null
}
    </div>
  );
}

export default Chat;
