import './CreateRoom.css';
import CustomInput from './CustomInput'
import Room from './RoomModel'
import React, { useState,  useEffect} from 'react';
import io from 'socket.io-client';




function CreateRoom(props) {

    const [roomName, setRoomName] = useState("")
    const [userName, setUserName] = useState("")
    const [password, setPassword] = useState("")
    const [roomCode, setRoomCode] = useState("")
    const [socket, setSocket] = useState("")



    useEffect(() => {

      let socket_ = io("http://192.168.1.9:3000", { transport: ['websocket']}) ;

      setSocket(socket_)
      socket.on('connect', (connection) => {

        //see if room exists
        socket.emit('generate-code')


        //if room does not exist
        socket.on('generate-code', (code) => {
          setRoomCode(code)
        })

      })

    }, []);

    function setValue(setter, event){
        setter(event.target.value)

    }

    function onCreateRoom(){

      //Create room object //create user object
      let room = new Room("25", roomName, userName)
      let user = {userName: userName}
      //update props
      props.data.user = user
      props.data.room = room;
      //change pages
      props.history.push("/chat?="+roomCode)


    }
  return (
    <div class="root">


      <div class="create-room-view">

            <CustomInput
                title="Name Your Room"
                placeholder="placeholder..."
                onChange={(event) => setValue(setRoomName, event)}
                value={roomName}
            />
            <CustomInput
                title="Create a username."
                placeholder="placeholder..."
                onChange={(event) => setValue(setUserName, event)}
                value={userName}
            />
            <CustomInput
                title="Create an admin password."
                placeholder="placeholder..."
                onChange={(event) => setValue(setPassword, event)}
                value={password}
            />

            <div class="share-view">
            <p class="caption">Share this code to your room.</p>
            <p class="caption-subtitle">{roomCode}</p>

            </div>



            <button onClick={onCreateRoom} class="btn">
                create
            </button>

      </div>

    </div>

  );
}

export default CreateRoom;
