import './ChatMessage.css';
import { FaGrin } from "react-icons/fa";
import{ useState} from 'react';



function ChatMessage(props) {

  const [showReactions, setShowReactions] = useState(false)
  const [reactions, setReactions] = useState([])
  let reactionOptions = ['0x1F602', '0x1F601' ]

  function onToggleReactions(){
    setShowReactions(!showReactions)
  }

  function onAddReaction(emoji){

      if(props.message.reactions[emoji])props.message.reactions[emoji]++
      else props.message.reactions[emoji] = 1
  }

  return (


    <div className={"chat-message-view "+ (props.isUser?"user":" ")}>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>

                <p class="username-text">{props.userName}</p> 
                {   Object.values(props.message.reactions).map((emoji)=>
                     <span class="username-text reaction-bar">{emoji}</span>
               )}




        <p onClick={props.onClick} className={"chat-message-text "+ (props.isUser?"user":" ")}>
            {props.text}
        </p>


    <div className="message-options-view">
      <span class={"reaction-options "+ (showReactions?"show":"hide")}>
               {   reactionOptions.map(
                     emoji =><span onClick={()=>onAddReaction(emoji)} className="reaction-option">{String.fromCodePoint(emoji)}</span>
               )}
            </span>
            <span onClick={onToggleReactions}className="show show-emojis-btn"><FaGrin/></span>

      </div>

    </div>
    );
}

export default ChatMessage;
