import { BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom';
import React, { useState,  useEffect} from 'react';
import { useHistory } from "react-router-dom";
import { withRouter } from "react-router";
import { Modal, Drawer, Button } from 'antd';
import "antd/dist/antd.css";




import styleSheet from '../Styles/StyleSheet'
import JoinRoom from './JoinRoom'
import CustomInput from './CustomInput'

import '../Styles/root-themes.css';
import './Landing.css';
import './LandingPrompts.css';


function LandingPrompt(props) {

  const [createAccountDrawerVisible, setCreateAccountDrawerVisible] = useState(false);

  const onCreateAccount = () => {
    console.log("clicked")
    setCreateAccountDrawerVisible(true);
  };


  const closeCreateAccountDrawer= () => {
    setCreateAccountDrawerVisible(false);
  };




  function removeToken(){
    localStorage.removeItem("persistentToken")
  }

  return (
      <div>
          <div class="">

          <div className="landing-header">
            <button className="root-theme-button-sm root-theme-bg-dark-blue register-button">create account</button>
          </div>



          <div class="login-options-view">
          <JoinRoom showPrompt={onCreateAccount} {...props}></JoinRoom>
          <h4 className="title-create-room">Create a room instead?</h4>
            <button  
              className="root-theme-button-sm button-create-room"
              onClick= {()=> {props.history.push("/createroom")}} 
            > 
              Create Room
            </button>

            <div className="landing-footer">
            </div>


          </div>


      </div>


    <Drawer 
        closable={true} 
        title="" 
        footer={null} 
        visible={createAccountDrawerVisible}
        getContainer={true}
        style={{ position: 'absolute' }}
        placement="bottom"
        height="600"

        >

          <CustomInput
                title="Username."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Password."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Number."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <CustomInput
                title="Access Code."
                placeholder="placeholder..."
                onChange={null}
                value={null}
            />
            <button >
              Create account.
            </button>
      </Drawer>
    </div>


  );
}

export default withRouter(LandingPrompt);


